text = input("Enter text to convert to Morse code: ").upper()

#Main Function for processing text to morse
def text_to_morse(text): 
    morse_code = []
    if not text:
        return 'ERROR : No input provided'
    for c in text:
        if c in morse_dict:
            morse_code.append(morse_dict[c]) # Letter Conversion
        elif c == ' ':
            morse_code.append('#') # Symbol for space between words
        else:
            morse_code.append('?') # Symbol for unsupported characters
    return '    '.join(morse_code)

#Final Variable for the Output in Morse
morse_output = text_to_morse(text)

# Display the Morse Code Output
print("Morse Code Output:")
print(morse_output)
print("\nThank you for using the Morse Code Converter!")